: space-around;\n  flex-wrap: wrap;\n  gap: 1rem;\n}\n\n.severity-box[_ngcontent-%COMP%] {\n  text-align: center;\n  padding: 0.5rem 1rem;\n  border-radius: 4px;\n  min-width: 120px;\n  display: flex;\n  flex-direction: column;\n  align-items: center;\n  justify-content: center;\n}\n\n.severity-box.critical[_ngcontent-%COMP%] {\n  background-color: #ffdde0;\n  color: #dc3545;\n}\n\n.severity-box.high[_ngcontent-%COMP%] {\n  background-color: #ffe7cc;\n  color: #fd7e14;\n}\n\n.severity-box.medium[_ngcontent-%COMP%] {\n  background-color: #fff7cc;\n  color: #ffc107;\n}\n\n.severity-box.low[_ngcontent-%COMP%] {\n  background-color: #d2fbe6;\n  color: #198754;\n}\n\n.severity-box.informative[_ngcontent-%COMP%] {\n  background-color: #d2f4fb;\n  color: #0dcaf0;\n}\n\n.severity-box.total[_ngcontent-%COMP%] {\n  background-color: #e0e0e0;\n  color: #222;\n  font-weight: bold;\n}\n\n.severity-box[_ngcontent-%COMP%]   .count[_ngcontent-%COMP%] {\n  font-size: 1.5rem;\n  font-weight: bold;\n  margin-bottom: 0.25rem;\n}\n\n.severity-box[_ngcontent-%COMP%]   .label[_ngcontent-%COMP%] {\n  font-size: 0.875rem;\n  opacity: 0.8;\n}\n\n@media (max-width: 900px) {\n  .severity-summary-boxes[_ngcontent-%COMP%] {\n    flex-wrap: wrap;\n    gap: 16px;\n  }\n  .severity-box[_ngcontent-%COMP%] {\n    min-width: 100px;\n    font-size: 1.1em;\n  }\n}\n/*# sourceMappingURL=data:application/json;charset=utf-8;base64, */"]
    });
