width: 50%;\n}\n.charts-loading-skeleton[_ngcontent-%COMP%]   .skeleton-line.w-30[_ngcontent-%COMP%] {\n  width: 30%;\n}\n.charts-loading-skeleton[_ngcontent-%COMP%]   .skeleton-card[_ngcontent-%COMP%] {\n  background: rgba(0, 212, 255, 0.05);\n  border-radius: 12px;\n  padding: 1.5rem;\n  margin-bottom: 1rem;\n  border: 1px solid rgba(0, 212, 255, 0.1);\n}\n.charts-loading-skeleton[_ngcontent-%COMP%]   .skeleton-circle[_ngcontent-%COMP%] {\n  width: 60px;\n  height: 60px;\n  border-radius: 50%;\n  background: linear-gradient(90deg, rgba(0, 212, 255, 0.1) 0%, rgba(0, 212, 255, 0.2) 50%, rgba(0, 212, 255, 0.1) 100%);\n  background-size: 200% 100%;\n  animation: _ngcontent-%COMP%_shimmer 1.5s ease-in-out infinite;\n  margin-bottom: 1rem;\n}\n.charts-loading-skeleton[_ngcontent-%COMP%]   .skeleton-chart-circle[_ngcontent-%COMP%] {\n  width: 200px;\n  height: 200px;\n  border-radius: 50%;\n  background: linear-gradient(90deg, rgba(0, 212, 255, 0.1) 0%, rgba(0, 212, 255, 0.2) 50%, rgba(0, 212, 255, 0.1) 100%);\n  background-size: 200% 100%;\n  animation: _ngcontent-%COMP%_shimmer 1.5s ease-in-out infinite;\n  margin: 0 auto;\n}\n.charts-loading-skeleton[_ngcontent-%COMP%]   .skeleton-chart-bars[_ngcontent-%COMP%] {\n  display: flex;\n  justify-content: space-around;\n  align-items: flex-end;\n  height: 200px;\n  padding: 1rem;\n}\n.charts-loading-skeleton[_ngcontent-%COMP%]   .skeleton-chart-bars[_ngcontent-%COMP%]   .skeleton-bar[_ngcontent-%COMP%] {\n  width: 30px;\n  background: linear-gradient(180deg, rgba(0, 212, 255, 0.2) 0%, rgba(0, 212, 255, 0.1) 100%);\n  border-radius: 4px;\n  animation: _ngcontent-%COMP%_pulse 2s ease-in-out infinite;\n  animation-delay: calc(var(--delay, 0) * 0.2s);\n}\n.charts-loading-skeleton[_ngcontent-%COMP%]   .skeleton-stats[_ngcontent-%COMP%] {\n  display: grid;\n  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));\n  gap: 1rem;\n  margin-bottom: 2rem;\n}\n.charts-loading-skeleton[_ngcontent-%COMP%]   .skeleton-charts[_ngcont