[_ngcontent-%COMP%]   .matrix-table[_ngcontent-%COMP%] {\n    min-width: 600px;\n  }\n  .form-grid[_ngcontent-%COMP%] {\n    grid-template-columns: 1fr;\n    gap: 1rem;\n  }\n  .form-section[_ngcontent-%COMP%] {\n    margin-bottom: 2rem;\n  }\n  .section-title[_ngcontent-%COMP%] {\n    font-size: 1.25rem;\n  }\n}\n@media (hover: none) and (pointer: coarse) {\n  .cyber-edit-button[_ngcontent-%COMP%], \n   .cyber-action-button[_ngcontent-%COMP%], \n   .submit-button[_ngcontent-%COMP%] {\n    min-height: 48px;\n    padding: 0.75rem 1.5rem;\n  }\n  .matrix-btn[_ngcontent-%COMP%], \n   .chart-btn[_ngcontent-%COMP%] {\n    min-height: 44px;\n    padding: 0.5rem 1rem;\n  }\n  .status-card[_ngcontent-%COMP%] {\n    padding: 1.5rem;\n  }\n  .form-input[_ngcontent-%COMP%], \n   .form-select[_ngcontent-%COMP%] {\n    min-height: 48px;\n    padding: 0.75rem 1rem;\n  }\n}\n.chart-wrapper[_ngcontent-%COMP%] {\n  contain: layout style paint;\n  will-change: transform;\n}\n.chart-wrapper[_ngcontent-%COMP%]   canvas[_ngcontent-%COMP%] {\n  max-width: 100%;\n  height: auto;\n}\n\n@media (prefers-reduced-motion: reduce) {\n  *[_ngcontent-%COMP%], \n   *[_ngcontent-%COMP%]::before, \n   *[_ngcontent-%COMP%]::after {\n    animation-duration: 0.01ms !important;\n    animation-iteration-count: 1 !important;\n    transition-duration: 0.01ms !important;\n  }\n  .cyber-chart-container[_ngcontent-%COMP%] {\n    transform: none !important;\n    opacity: 1 !important;\n  }\n}\n@media (prefers-contrast: high) {\n  .cyber-dashboard-header[_ngcontent-%COMP%] {\n    border-color: #ffffff;\n  }\n  .status-card[_ngcontent-%COMP%] {\n    border-color: #ffffff;\n  }\n  .cyber-chart-container[_ngcontent-%COMP%] {\n    border-color: #ffffff;\n  }\n  .form-input[_ngcontent-%COMP%], \n   .form-select[_ngcontent-%COMP%] {\n    border-color: #ffffff;\n  }\n}\n.security-alerts-section[_ngcontent-%COMP%] {\n  margin-bottom: 3rem;\n  padding: 0 1rem;\n}\n@media (max-width: 768px) {\n  .security-alerts-section[_ngcontent-%COMP%] {\n    padding: 0 0.5rem;\n    margin-bottom: 2rem;\n  }\n}\n\n.dashboard-wrapper[_ngcontent-%COMP%] {\n  position: relative;\n}\n.dashboard-wrapper[_ngcontent-%COMP%]::before {\n  content: \"\";\n  position: fixed;\n  top: 0;\n  left: 0;\n  right: 0;\n  bottom: 0;\n  background: url('data:image/svg+xml,<svg xmlns=\"http://www.w3.org/2000/svg\" viewBox=\"0 0 100 100\"><defs><pattern id=\"grid\" width=\"10\" height=\"10\" patternUnits=\"userSpaceOnUse\"><path d=\"M 10 0 L 0 0 0 10\" fill=\"none\" stroke=\"rgba(0,212,255,0.05)\" stroke-width=\"0.5\"/></pattern></defs><rect width=\"100\" height=\"100\" fill=\"url(%23grid)\" /></svg>') repeat;\n  opacity: 0.3;\n  pointer-events: none;\n  z-index: -1;\n}\n\n\n\n.validation-popup[_ngcontent-%COMP%] {\n  position: fixed;\n  top: 20px;\n  right: 20px;\n  z-index: 1000;\n  animation: _ngcontent-%COMP%_slideIn 0.3s ease-out;\n}\n\n.validation-content[_ngcontent-%COMP%] {\n  background: rgba(31, 41, 55, 0.95);\n  -webkit-backdrop-filter: blur(8px);\n          backdrop-filter: blur(8px);\n  border: 1px solid rgba(75, 85, 99, 0.5);\n  border-radius: 12px;\n  padding: 1rem 1.5rem;\n  display: flex;\n  align-items: center;\n  gap: 1rem;\n  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);\n  min-width: 300px;\n  max-width: 400px;\n}\n\n.validation-icon[_ngcontent-%COMP%] {\n  flex-shrink: 0;\n  width: 24px;\n  height: 24px;\n  color: #f87171;\n}\n.validation-icon[_ngcontent-%COMP%]   svg[_ngcontent-%COMP%] {\n  width: 100%;\n  height: 100%;\n}\n\n.validation-message[_ngcontent-%COMP%] {\n  color: #e5e7eb;\n  font-size: 0.9375rem;\n  font-weight: 500;\n  line-height: 1.5;\n}\n\n@keyframes _ngcontent-%COMP%_slideIn {\n  from {\n    transform: translateX(100%);\n    opacity: 0;\n  }\n  to {\n    transform: translateX(0);\n    opacity: 1;\n  }\n}\n/*# sourceMappingURL=data:application/json;charset=utf-8;base64, */"],
